import React, { useEffect, useState } from 'react';

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/components')
      .then(res => res.json())
      .then(data => {
        console.log(data);  // –î–ª—è –æ—Ç–ª–∞–¥–∫–∏
        setComponents(data);
      })
      .catch(err => console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err));
  }, []);

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget)) return;  // –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ –≤–≤–µ–¥–µ–Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞—è —Å—É–º–º–∞

    const fullConfigs = [];
    const tempClosestConfigs = [];
    
    // –§–∏–ª—å—Ç—Ä—É–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
    const categories = ['processor', 'graphicsCard', 'ram', 'storage', 'motherboard'];

    // –°–æ–±–∏—Ä–∞–µ–º –≤—Å–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã, –ø–æ–¥—Ö–æ–¥—è—â–∏–µ –ø–æ –±—é–¥–∂–µ—Ç—É
    const selectedComponents = categories.reduce((acc, category) => {
      acc[category] = [];
      components.forEach(item => {
        if (item.category === category && item.price <= maxBudget) {
          acc[category].push(item);
        }
      });
      return acc;
    }, {});

    let totalPrice = 0;
    let hasFullConfig = true;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –º–æ–∂–µ–º –ª–∏ —Å–æ–±—Ä–∞—Ç—å –ø–æ–ª–Ω—É—é –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é
    for (let category of categories) {
      if (selectedComponents[category].length === 0) {
        hasFullConfig = false;
        break;
      }
    }

    // –ï—Å–ª–∏ –ø–æ–ª–Ω–∞—è –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –Ω–∞–π–¥–µ–Ω–∞
    if (hasFullConfig) {
      const fullConfig = {};
      categories.forEach(category => {
        fullConfig[category] = selectedComponents[category][0];  // –í—ã–±–∏—Ä–∞–µ–º –ø–µ—Ä–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –∏–∑ –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö
        totalPrice += fullConfig[category].price;
      });
      if (totalPrice <= maxBudget) {
        fullConfigs.push(fullConfig);
      }
    }

    // –ï—Å–ª–∏ –ø–æ–ª–Ω—ã—Ö –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–π –Ω–µ—Ç, –Ω–∞—Ö–æ–¥–∏–º –±–æ–ª–µ–µ –¥–æ—Ä–æ–≥–∏–µ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏
    if (fullConfigs.length === 0) {
      components.forEach(item => {
        const tempConfig = { ...selectedComponents };
        tempConfig[item.category] = item;
        let configPrice = 0;
        categories.forEach(category => {
          if (tempConfig[category]) {
            configPrice += tempConfig[category].price;
          }
        });
        if (configPrice > maxBudget && configPrice <= maxBudget + 20000) {
          tempClosestConfigs.push(tempConfig);
        }
      });
    }

    setConfigurations(fullConfigs);
    setClosestConfigs(tempClosestConfigs);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">üõ† –ü–æ–¥–±–æ—Ä–∫–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</h2>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="–í–≤–µ–¥–∏—Ç–µ –±—é–¥–∂–µ—Ç (—Ç–µ–Ω–≥–µ)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
          />
          <button
            onClick={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            –ü–æ–¥–æ–±—Ä–∞—Ç—å
          </button>
        </div>

        {/* –ü–æ–ª–Ω—ã–µ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–¥—Ö–æ–¥—è—Ç –ø–æ–¥ –±—é–¥–∂–µ—Ç */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ –ø–æ –≤–∞—à–µ–º—É –±—é–¥–∂–µ—Ç—É:</h3>
            {configurations.map((config, index) => {
              let totalPrice = 0;
              categories.forEach(category => {
                totalPrice += config[category].price;
              });

              return (
                <div key={index} className="mb-4">
                  <ul>
                    {Object.keys(config).map((key, i) => {
                      const component = config[key];
                      return (
                        <li key={i} className="text-lg">
                          <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {component.name} 
                          <span className="italic text-gray-500">({component.description})</span>
                        </li>
                      );
                    })}
                  </ul>
                  <p className="font-bold text-lg mt-2">–û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {totalPrice} ‚Ç∏</p>
                </div>
              );
            })}
          </div>
        ) : (
          <p className="text-gray-600">–ù–µ—Ç –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö –∫–æ–º–ø–ª–µ–∫—Ç—É—é—â–∏—Ö –¥–ª—è –≤–∞—à–µ–≥–æ –±—é–¥–∂–µ—Ç–∞.</p>
        )}

        {/* –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –Ω–µ–º–Ω–æ–≥–æ –¥–æ—Ä–æ–∂–µ –±—é–¥–∂–µ—Ç–∞ */}
        {closestConfigs.length > 0 ? (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞—é—Ç –≤–∞—à –±—é–¥–∂–µ—Ç:</h3>
            {closestConfigs.map((config, index) => {
              let totalPrice = 0;
              categories.forEach(category => {
                totalPrice += config[category].price;
              });

              return (
                <div key={index} className="mb-4">
                  <ul>
                    {Object.keys(config).map((key, i) => {
                      const component = config[key];
                      return (
                        <li key={i} className="text-lg">
                          <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {component.name} 
                          <span className="italic text-gray-500">({component.description})</span>
                        </li>
                      );
                    })}
                  </ul>
                  <p className="font-bold text-lg mt-2">–û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {totalPrice} ‚Ç∏</p>
                  <p className="text-gray-600 mt-2">–≠—Ç–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞–µ—Ç –≤–∞—à –±—é–¥–∂–µ—Ç.</p>
                </div>
              );
            })}
          </div>
        ) : null}
      </div>
    </div>
  );
}

export default PCBuilder;
