import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PCBuilder = () => {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchComponents = async () => {
      const response = await axios.get('http://localhost:3001/components');
      setComponents(response.data);

      const uniqueCategories = [...new Set(response.data.map(item => item.category))];
      setCategories(uniqueCategories);
    };

    fetchComponents();
  }, []);

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget)) return;

    const grouped = categories.reduce((acc, category) => {
      acc[category] = components.filter(item => item.category === category);
      return acc;
    }, {});

    const allCombinations = [];
    for (let cpu of grouped.processor || []) {
      for (let gpu of grouped.graphicsCard || []) {
        for (let ram of grouped.ram || []) {
          for (let storage of grouped.storage || []) {
            for (let mb of grouped.motherboard || []) {
              const config = { processor: cpu, graphicsCard: gpu, ram, storage, motherboard: mb };
              const totalPrice =
                cpu.price + gpu.price + ram.price + storage.price + mb.price;
              allCombinations.push({ config, totalPrice });
            }
          }
        }
      }
    }

    const withinBudget = allCombinations.filter(c => c.totalPrice <= maxBudget);

    if (withinBudget.length > 0) {
      const sorted = withinBudget.sort((a, b) => a.totalPrice - b.totalPrice);
      setConfigurations(sorted.slice(0, 3));
      setClosestConfigs([]);
    } else {
      const overBudget = allCombinations
        .filter(c => c.totalPrice > maxBudget)
        .sort((a, b) => a.totalPrice - b.totalPrice);
      setConfigurations([]);
      setClosestConfigs(overBudget.slice(0, 3));
    }
  };

  const renderConfig = ({ config, totalPrice }, index) => (
    <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
      <p><strong>Процессор:</strong> {config.processor.name} ({config.processor.description})</p>
      <p><strong>Видеокарта:</strong> {config.graphicsCard.name} ({config.graphicsCard.description})</p>
      <p><strong>ОЗУ:</strong> {config.ram.name} ({config.ram.description})</p>
      <p><strong>Накопитель:</strong> {config.storage.name} ({config.storage.description})</p>
      <p><strong>Материнская плата:</strong> {config.motherboard.name} ({config.motherboard.description})</p>
      <p><strong>Общая стоимость:</strong> {totalPrice.toLocaleString()} ₸</p>
    </div>
  );

  return (
    <div>
      <h1>Сборка ПК по бюджету</h1>
      <input
        type="number"
        placeholder="Введите бюджет, ₸"
        value={budget}
        onChange={(e) => setBudget(e.target.value)}
      />
      <button onClick={handleFilter}>Подобрать</button>

      {configurations.length > 0 && (
        <>
          <h2>Конфигурации по вашему бюджету:</h2>
          {configurations.map(renderConfig)}
        </>
      )}

      {configurations.length === 0 && closestConfigs.length > 0 && (
        <>
          <h2>Под ваш бюджет ничего не найдено. Вот ближайшие более дорогие варианты:</h2>
          {closestConfigs.map(renderConfig)}
        </>
      )}

      {configurations.length === 0 && closestConfigs.length === 0 && (
        <p>Нет подходящих конфигураций.</p>
      )}
    </div>
  );
};

export default PCBuilder;
