import React, { useEffect, useState } from 'react';

// Объект с переводами категорий на русский язык
const categoryTranslations = {
  processor: 'Процессор',
  graphicsCard: 'Видеокарта',
  ram: 'Оперативная память',
  storage: 'Накопитель',
  motherboard: 'Материнская плата',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const categories = ['processor', 'graphicsCard', 'ram', 'storage', 'motherboard'];

  // Загрузка компонентов с API
  useEffect(() => {
    setLoading(true);
    fetch('http://localhost:3001/api/components')
      .then(res => {
        if (!res.ok) throw new Error('Ошибка загрузки компонентов');
        return res.json();
      })
      .then(data => {
        console.log('Загруженные компоненты:', data); // Для отладки
        setComponents(data);
        setError(null);
      })
      .catch(err => {
        setError(err.message);
        console.error('Ошибка загрузки:', err);
      })
      .finally(() => setLoading(false));
  }, []);

  // Валидация и обработка фильтрации
  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('Пожалуйста, введите корректный положительный бюджет.');
      return;
    }
    setError(null);

    const fullConfigs = [];
    const tempClosestConfigs = [];

    // Фильтрация компонентов по категориям
    const selectedComponents = categories.reduce((acc, category) => {
      acc[category] = components.filter(
        item => item.category === category && item.price <= maxBudget
      );
      return acc;
    }, {});

    // Проверка наличия компонентов для каждой категории
    const hasFullConfig = categories.every(category => selectedComponents[category].length > 0);

    if (hasFullConfig) {
      // Генерация комбинаций конфигураций
      const combinations = generateCombinations(selectedComponents, maxBudget);
      fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
      tempClosestConfigs.push(
        ...combinations.filter(
          config => config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 20000
        )
      );
    }

    // Сортировка конфигураций по производительности и цене
    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3) // Ограничение до 3 лучших конфигураций
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
  };

  // Генерация комбинаций конфигураций с учетом совместимости
  const generateCombinations = (componentsByCategory, maxBudget) => {
    const results = [];

    // Рекурсивная функция для перебора комбинаций
    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (categoryIndex >= categories.length) {
        if (totalPrice <= maxBudget + 20000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = categories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        // Проверка совместимости процессора и материнской платы
        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        const newPrice = totalPrice + component.price;
        const newPerformance = totalPerformance + (component.performance || 0);

        if (newPrice <= maxBudget + 20000) {
          buildConfig(
            { ...currentConfig, [category]: component },
            categoryIndex + 1,
            newPrice,
            newPerformance
          );
        }
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        {/* Поле ввода бюджета */}
        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
            min="0"
          />
          <button
            onClick={handleFilter}
            disabled={loading}
            className={`bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 ${
              loading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {loading ? 'Загрузка...' : 'Подобрать'}
          </button>
        </div>

        {/* Отображение ошибок */}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {/* Полные конфигурации */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
              </div>
            ))}
          </div>
        ) : (
          !error && !loading && (
            <p className="text-gray-600">Нет подходящих комплектующих для вашего бюджета.</p>
          )
        )}

        {/* Конфигурации, превышающие бюджет */}
        {closestConfigs.length > 0 && (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">
              Конфигурации, которые немного превышают ваш бюджет:
            </h3>
            {closestConfigs.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
                <p className="text-gray-600 mt-2">
                  Эта конфигурация немного превышает ваш бюджет.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
