import React, { useEffect, useState } from 'react';

const categoryTranslations = {
  processor: '–ü—Ä–æ—Ü–µ—Å—Å–æ—Ä',
  graphicsCard: '–í–∏–¥–µ–æ–∫–∞—Ä—Ç–∞',
  ram: '–û–ø–µ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–∞–º—è—Ç—å',
  storage: '–ù–∞–∫–æ–ø–∏—Ç–µ–ª—å',
  motherboard: '–ú–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞',
  case: '–ö–æ—Ä–ø—É—Å',
  cooler: '–ö—É–ª–µ—Ä',
  monitor: '–ú–æ–Ω–∏—Ç–æ—Ä',
  powerSupply: '–ë–ª–æ–∫ –ø–∏—Ç–∞–Ω–∏—è',
  keyboard: '–ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞',
  mouse: '–ú—ã—à—å',
  operatingSystem: '–û–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState({
    processor: true,
    graphicsCard: true,
    ram: true,
    storage: true,
    motherboard: true,
    case: true,
    cooler: true,
    monitor: true,
    powerSupply: true,
    keyboard: true,
    mouse: true,
    operatingSystem: true,
  });

  const categories = Object.keys(categoryTranslations);

  const normalizeComponent = (item) => {
    return {
      ...item,
      category: item.category?.toLowerCase().trim() || '',
      price: Number(item.price) || 0,
      performance: Number(item.performance) || 0,
      socket: item.socket || '',
      power: Number(item.power) || 0,
      formFactor: item.formFactor || '',
      resolution: item.resolution || '',
      wattage: Number(item.wattage) || 0,
      type: item.type || '',
      version: item.version || '',
    };
  };

  useEffect(() => {
    const fetchComponents = async (attempt = 1, maxAttempts = 3) => {
      setLoading(true);
      try {
        const response = await fetch('http://localhost:3001/api/components');
        if (!response.ok) throw new Error(`HTTP –æ—à–∏–±–∫–∞: ${response.status}`);
        const data = await response.json();
        console.log('–ó–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:', data);

        if (!Array.isArray(data) || data.length === 0) {
          setError('–ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –¥–∞–Ω–Ω—ã–µ –≤ Google Sheets.');
          setComponents([]);
          return;
        }

        const normalizedData = data
          .map(normalizeComponent)
          .filter(item => item.category && item.price > 0);

        setComponents(normalizedData);
        setError(null);
      } catch (err) {
        console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err);
        if (attempt < maxAttempts) {
          console.log(`–ü–æ–≤—Ç–æ—Ä–Ω–∞—è –ø–æ–ø—ã—Ç–∫–∞ ${attempt + 1}/${maxAttempts} —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥—ã...`);
          setTimeout(() => fetchComponents(attempt + 1, maxAttempts), 2000);
        } else {
          setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É.');
          setComponents([]);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchComponents();
  }, []);

  const handleCategoryToggle = (category) => {
    setSelectedCategories(prev => ({
      ...prev,
      [category]: !prev[category],
    }));
  };

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–π –±—é–¥–∂–µ—Ç.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }
    if (maxBudget < 200000) {
      setError('–î–ª—è –¥–∞–Ω–Ω–æ–π —Å—É–º–º—ã –Ω–µ—Ç –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏. –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –±—é–¥–∂–µ—Ç: 200,000 ‚Ç∏.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    setError(null);
    setLoading(true);

    if (!components.length) {
      setError('–ö–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É.');
      setLoading(false);
      return;
    }

    const activeCategories = categories.filter(cat => selectedCategories[cat]);
    if (!activeCategories.length) {
      setError('–í—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–Ω—É –∫–∞—Ç–µ–≥–æ—Ä–∏—é.');
      setLoading(false);
      return;
    }

    const selected = {};
    for (const category of activeCategories) {
      selected[category] = components.filter(c => c.category === category && c.price <= maxBudget);
    }

    const allPresent = activeCategories.every(cat => selected[cat].length > 0);
    if (!allPresent) {
      const missing = activeCategories.filter(cat => selected[cat].length === 0);
      setError(`–ù–µ –Ω–∞–π–¥–µ–Ω—ã –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –¥–ª—è –∫–∞—Ç–µ–≥–æ—Ä–∏–π: ${missing.map(c => categoryTranslations[c]).join(', ')}`);
      setLoading(false);
      return;
    }

    const allConfigs = generateCombinations(selected, maxBudget, activeCategories);
    const withinBudget = allConfigs.filter(cfg => cfg.totalPrice <= maxBudget);
    const overBudget = allConfigs.filter(cfg => cfg.totalPrice > maxBudget && cfg.totalPrice <= maxBudget + 100000);

    setConfigurations(
      withinBudget.sort((a, b) => b.totalPerformance - a.totalPerformance).slice(0, 3)
    );
    setClosestConfigs(
      overBudget.sort((a, b) => b.totalPerformance - a.totalPerformance).slice(0, 3)
    );

    setLoading(false);
  };

  const generateCombinations = (byCategory, maxBudget, activeCategories) => {
    const results = [];
    const maxComb = 1000;

    const build = (current, index, price, perf) => {
      if (results.length >= maxComb) return;
      if (index >= activeCategories.length) {
        results.push({ ...current, totalPrice: price, totalPerformance: perf });
        return;
      }

      const category = activeCategories[index];
      for (const comp of byCategory[category]) {
        if (price + comp.price > maxBudget + 100000) continue;

        if (category === 'motherboard' && current.processor && comp.socket !== current.processor.socket) continue;
        if (category === 'cooler' && current.processor && !comp.socket.split(',').includes(current.processor.socket)) continue;
        if (category === 'case' && current.motherboard && !comp.formFactor.split(',').map(f => f.trim()).includes(current.motherboard.formFactor)) continue;
        if (category === 'powerSupply' && current.processor && current.graphicsCard) {
          const need = current.processor.power + current.graphicsCard.power + 100;
          if (comp.wattage < need) continue;
        }

        build({ ...current, [category]: comp }, index + 1, price + comp.price, perf + (comp.performance || 0));
      }
    };

    build({}, 0, 0, 0);
    return results;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">üõ† –ü–æ–¥–±–æ—Ä–∫–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</h2>

        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2">–í—ã–±–µ—Ä–∏—Ç–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:</h3>
          <div className="grid grid-cols-2 gap-2">
            {categories.map(category => (
              <label key={category} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={selectedCategories[category]}
                  onChange={() => handleCategoryToggle(category)}
                  className="form-checkbox h-5 w-5 text-blue-600"
                />
                <span>{categoryTranslations[category]}</span>
              </label>
            ))}
          </div>
        </div>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="–í–≤–µ–¥–∏—Ç–µ –±—é–¥–∂–µ—Ç (—Ç–µ–Ω–≥–µ)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
            min="0"
          />
          <button
            onClick={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            –ü–æ–¥–æ–±—Ä–∞—Ç—å
          </button>
        </div>

        {loading && <p className="text-blue-500 mb-4">–ó–∞–≥—Ä—É–∑–∫–∞...</p>}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {configurations.length > 0 && (
          <div>
            <h3 className="font-bold text-xl mb-4">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ –ø–æ –≤–∞—à–µ–º—É –±—é–¥–∂–µ—Ç—É:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  –û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {config.totalPrice} ‚Ç∏
                </p>
                <p className="text-gray-600">
                  –ü—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: {config.totalPerformance} (—É—Å–ª–æ–≤–Ω—ã–µ –µ–¥–∏–Ω–∏—Ü—ã)
                </p>
              </div>
            ))}
          </div>
        )}

        {closestConfigs.length > 0 && (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞—é—Ç –≤–∞—à –±—é–¥–∂–µ—Ç:</h3>
            {closestConfigs.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  –û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {config.totalPrice} ‚Ç∏
                </p>
                <p className="text-gray-600">
                  –ü—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: {config.totalPerformance} (—É—Å–ª–æ–≤–Ω—ã–µ –µ–¥–∏–Ω–∏—Ü—ã)
                </p>
                <p className="text-gray-600 mt-2">
                  –≠—Ç–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞–µ—Ç –≤–∞—à –±—é–¥–∂–µ—Ç.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
