import React, { useEffect, useState } from 'react';

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/components')
      .then(res => res.json())
      .then(data => setComponents(data))
      .catch(err => console.error('Ошибка загрузки:', err));
  }, []);

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget) return;

    const fullConfigs = [];
    const closestConfigs = [];
    
    // Сортируем компоненты по цене
    const sorted = [...components].sort((a, b) => a.price - b.price);
    
    // Пробуем подобрать полные конфигурации, которые укладываются в бюджет
    const selectedComponents = {
      processor: null,
      graphicsCard: null,
      ram: null,
      storage: null,
    };
    
    let totalPrice = 0;

    // Собираем полные конфигурации
    for (let item of sorted) {
      if (!selectedComponents[item.category] && totalPrice + item.price <= maxBudget) {
        selectedComponents[item.category] = item;
        totalPrice += item.price;
      }
    }

    // Если полная конфигурация найдена
    if (selectedComponents.processor && selectedComponents.graphicsCard && selectedComponents.ram && selectedComponents.storage) {
      fullConfigs.push(selectedComponents);
    }

    // Теперь ищем конфигурации, которые дороже бюджета
    for (let item of sorted) {
      let tempConfig = {
        processor: null,
        graphicsCard: null,
        ram: null,
        storage: null,
      };

      let configPrice = 0;

      for (let comp of sorted) {
        if (!tempConfig[comp.category] && configPrice + comp.price <= maxBudget + 20000) {
          tempConfig[comp.category] = comp;
          configPrice += comp.price;
        }
      }

      if (configPrice > maxBudget && configPrice <= maxBudget + 20000) {
        closestConfigs.push(tempConfig);
      }
    }

    // Если полных конфигураций нет, показываем те, что наиболее близки к бюджету
    if (fullConfigs.length === 0 && closestConfigs.length === 0) {
      const sortedByPrice = [...components].sort((a, b) => a.price - b.price);
      let tempClosest = [];
      for (let i = 0; i < 3 && i < sortedByPrice.length; i++) {
        if (sortedByPrice[i].price > maxBudget) {
          tempClosest.push(sortedByPrice[i]);
        }
      }
      setConfigurations([]);
      setClosestConfigs(tempClosest);
    } else {
      setConfigurations(fullConfigs);
      setClosestConfigs(closestConfigs);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
          />
          <button
            onClick={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Подобрать
          </button>
        </div>

        {/* Полные конфигурации, которые подходят под бюджет */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-4">
                <ul>
                  {Object.keys(config).map((key, i) => (
                    <li key={i} className="text-lg">
                      <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {config[key].name} (Цена: {config[key].price} ₸)
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-600">Нет подходящих комплектующих для вашего бюджета.</p>
        )}

        {/* Конфигурации, которые немного дороже бюджета */}
        {closestConfigs.length > 0 ? (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">Конфигурации, которые немного превышают ваш бюджет:</h3>
            {closestConfigs.map((config, index) => (
              <div key={index} className="mb-4">
                <ul>
                  {Object.keys(config).map((key, i) => (
                    <li key={i} className="text-lg">
                      <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {config[key].name} (Цена: {config[key].price} ₸)
                    </li>
                  ))}
                </ul>
                <p className="text-gray-600 mt-2">Эта конфигурация немного превышает ваш бюджет.</p>
              </div>
            ))}
          </div>
        ) : null}
      </div>
    </div>
  );
}

export default PCBuilder;
