import React, { useEffect, useState } from 'react';

// –û–±—ä–µ–∫—Ç —Å –ø–µ—Ä–µ–≤–æ–¥–∞–º–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–π –Ω–∞ —Ä—É—Å—Å–∫–∏–π —è–∑—ã–∫
const categoryTranslations = {
  processor: '–ü—Ä–æ—Ü–µ—Å—Å–æ—Ä',
  graphicsCard: '–í–∏–¥–µ–æ–∫–∞—Ä—Ç–∞',
  ram: '–û–ø–µ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–∞–º—è—Ç—å',
  storage: '–ù–∞–∫–æ–ø–∏—Ç–µ–ª—å',
  motherboard: '–ú–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞',
  case: '–ö–æ—Ä–ø—É—Å',
  cooler: '–ö—É–ª–µ—Ä',
  monitor: '–ú–æ–Ω–∏—Ç–æ—Ä',
  powerSupply: '–ë–ª–æ–∫ –ø–∏—Ç–∞–Ω–∏—è',
  keyboard: '–ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞',
  mouse: '–ú—ã—à—å',
  operatingSystem: '–û–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState({
    processor: true,
    graphicsCard: true,
    ram: true,
    storage: true,
    motherboard: true,
    case: true,
    cooler: true,
    monitor: true,
    powerSupply: true,
    keyboard: true,
    mouse: true,
    operatingSystem: true,
  });

  const categories = [
    'processor',
    'graphicsCard',
    'ram',
    'storage',
    'motherboard',
    'case',
    'cooler',
    'monitor',
    'powerSupply',
    'keyboard',
    'mouse',
    'operatingSystem',
  ];

  // –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö
  const normalizeComponent = (item) => {
    const normalized = {
      ...item,
      category: item.category ? item.category.trim() : '',
      price: Number(item.price) || 0,
      performance: Number(item.performance) || 0,
      socket: item.socket ? item.socket.trim() : '',
      power: Number(item.power) || 0,
      formFactor: item.formFactor ? item.formFactor.trim() : '',
      resolution: item.resolution ? item.resolution.trim() : '',
      wattage: Number(item.wattage) || 0,
      type: item.type ? item.type.trim() : '',
      version: item.version ? item.version.trim() : '',
    };
    console.log('–ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞:', item, '‚Üí', normalized);
    return normalized;
  };

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ —Å API
  useEffect(() => {
    const API_URL = 'http://localhost:3001/api/components'; // –ü–æ–ø—Ä–æ–±—É–π—Ç–µ 'http://192.168.1.100:3001/api/components' –µ—Å–ª–∏ localhost –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç
    const fetchComponents = async (attempt = 1, maxAttempts = 3) => {
      setLoading(true);
      try {
        console.log(`–ü–æ–ø—ã—Ç–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ (${attempt}/${maxAttempts}) —Å ${API_URL}...`);
        const response = await fetch(API_URL, {
          signal: AbortSignal.timeout(5000), // –¢–∞–π–º–∞—É—Ç 5 —Å–µ–∫—É–Ω–¥
        });
        if (!response.ok) {
          throw new Error(`HTTP –æ—à–∏–±–∫–∞: ${response.status} ${response.statusText}`);
        }
        const data = await response.json();
        console.log('–ó–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:', data);
        if (!Array.isArray(data) || data.length === 0) {
          setError('–ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –¥–∞–Ω–Ω—ã–µ –≤ Google Sheets.');
          setComponents([]);
          return;
        }
        const normalizedData = data
          .map(normalizeComponent)
          .filter(item => item.category && item.price > 0);
        console.log('–ù–æ—Ä–º–∞–ª–∏–∑–æ–≤–∞–Ω–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:', normalizedData);
        if (normalizedData.length === 0) {
          setError('–ü–æ—Å–ª–µ –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ —Ñ–æ—Ä–º–∞—Ç –¥–∞–Ω–Ω—ã—Ö.');
          setComponents([]);
          return;
        }
        setComponents(normalizedData);
        setError(null);
      } catch (err) {
        console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err.message);
        if (attempt < maxAttempts) {
          console.log(`–ü–æ–≤—Ç–æ—Ä–Ω–∞—è –ø–æ–ø—ã—Ç–∫–∞ ${attempt + 1}/${maxAttempts} —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥—ã...`);
          setTimeout(() => fetchComponents(attempt + 1, maxAttempts), 2000);
        } else {
          setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö: ${err.message}. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É –ø–æ ${API_URL}.`);
          setComponents([]);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchComponents();
  }, []);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–π
  const handleCategoryToggle = (category) => {
    setSelectedCategories(prev => ({
      ...prev,
      [category]: !prev[category],
    }));
  };

  // –í–∞–ª–∏–¥–∞—Ü–∏—è –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–π –±—é–¥–∂–µ—Ç.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }
    if (maxBudget < 200000) {
      setError('–î–ª—è –¥–∞–Ω–Ω–æ–π —Å—É–º–º—ã –Ω–µ—Ç –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏. –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –±—é–¥–∂–µ—Ç: 200,000 ‚Ç∏.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }
    setError(null);
    setLoading(true);

    console.log('–í—Å–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –ø–µ—Ä–µ–¥ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–µ–π:', components);
    console.log('–ë—é–¥–∂–µ—Ç:', maxBudget);
    console.log('–í—ã–±—Ä–∞–Ω–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏:', selectedCategories);

    if (!components.length) {
      setError('–ö–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É.');
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    const fullConfigs = [];
    const tempClosestConfigs = [];

    // –ü–æ–ª—É—á–∞–µ–º –∞–∫—Ç–∏–≤–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
    const activeCategories = categories.filter(category => selectedCategories[category]);
    if (activeCategories.length === 0) {
      setError('–í—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–Ω—É –∫–∞—Ç–µ–≥–æ—Ä–∏—é.');
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
    const selectedComponents = activeCategories.reduce((acc, category) => {
      acc[category] = components.filter(item => {
        const isValidCategory = item.category === category;
        const isValidPrice = typeof item.price === 'number' && item.price > 0 && item.price <= maxBudget;
        console.log(
          `–§–∏–ª—å—Ç—Ä–∞—Ü–∏—è ${category}:`,
          item.name || '–ë–µ–∑ –∏–º–µ–Ω–∏',
          'category match:', isValidCategory,
          'price valid:', isValidPrice,
          'item:', item
        );
        return isValidCategory && isValidPrice;
      });
      console.log(
        `–ö–∞—Ç–µ–≥–æ—Ä–∏—è ${category}: ${acc[category].length} –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤`,
        acc[category].map(item => `${item.name}: ${item.price} ‚Ç∏`)
      );
      return acc;
    }, {});

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
    const hasFullConfig = activeCategories.every(category => selectedComponents[category].length > 0);
    if (!hasFullConfig) {
      const emptyCategories = activeCategories.filter(
        category => selectedComponents[category].length === 0
      );
      setError(
        `–ù–µ —É–¥–∞–ª–æ—Å—å —Å—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞—Ç—å –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏: –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –≤ –∫–∞—Ç–µ–≥–æ—Ä–∏—è—Ö: ${emptyCategories
          .map(cat => categoryTranslations[cat])
          .join(', ')}.`
      );
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–º–±–∏–Ω–∞—Ü–∏–π
    const combinations = generateCombinations(selectedComponents, maxBudget, activeCategories);
    fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
    tempClosestConfigs.push(
      ...combinations.filter(
        config => config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 100000
      )
    );

    // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–π
    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setLoading(false);
  };

  // –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–∞—è –≥–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–º–±–∏–Ω–∞—Ü–∏–π
  const generateCombinations = (componentsByCategory, maxBudget, activeCategories) => {
    const results = [];
    const maxCombinations = 1000;

    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (results.length >= maxCombinations) {
        console.warn('–î–æ—Å—Ç–∏–≥–Ω—É—Ç –ª–∏–º–∏—Ç –∫–æ–º–±–∏–Ω–∞—Ü–∏–π:', results.length);
        return;
      }

      if (categoryIndex >= activeCategories.length) {
        if (totalPrice <= maxBudget + 100000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = activeCategories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        if (totalPrice + component.price > maxBudget + 100000) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –º–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞ –∏ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä
        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          selectedCategories.motherboard &&
          selectedCategories.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –∫—É–ª–µ—Ä –∏ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä
        if (
          category === 'cooler' &&
          currentConfig.processor &&
          selectedCategories.cooler &&
          selectedCategories.processor &&
          !component.socket.split(',').includes(currentConfig.processor.socket)
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –∫–æ—Ä–ø—É—Å –∏ –º–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞
        if (
          category === 'case' &&
          currentConfig.motherboard &&
          selectedCategories.case &&
          selectedCategories.motherboard &&
          component.formFactor &&
          !component.formFactor.split(',').map(f => f.trim()).includes(currentConfig.motherboard.formFactor)
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –±–ª–æ–∫ –ø–∏—Ç–∞–Ω–∏—è
        if (
          category === 'powerSupply' &&
          selectedCategories.powerSupply &&
          currentConfig.processor &&
          selectedCategories.processor &&
          currentConfig.graphicsCard &&
          selectedCategories.graphicsCard
        ) {
          const totalPower = currentConfig.processor.power + currentConfig.graphicsCard.power + 100;
          if (component.wattage < totalPower) {
            return;
          }
        }

        const newPrice = totalPrice + component.price;
        const newPerformance = totalPerformance + (component.performance || 0);

        buildConfig(
          { ...currentConfig, [category]: component },
          categoryIndex + 1,
          newPrice,
          newPerformance
        );
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">üõ† –ü–æ–¥–±–æ—Ä–∫–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞</h2>

        {/* –í—ã–±–æ—Ä –∫–∞—Ç–µ–≥–æ—Ä–∏–π */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2">–í—ã–±–µ—Ä–∏—Ç–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:</h3>
          <div className="grid grid-cols-2 gap-2">
            {categories.map(category => (
              <label key={category} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={selectedCategories[category]}
                  onChange={() => handleCategoryToggle(category)}
                  className="form-checkbox h-5 w-5 text-blue-600"
                />
                <span>{categoryTranslations[category]}</span>
              </label>
            ))}
          </div>
        </div>

        {/* –ü–æ–ª–µ –≤–≤–æ–¥–∞ –±—é–¥–∂–µ—Ç–∞ */}
        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="–í–≤–µ–¥–∏—Ç–µ –±—é–¥–∂–µ—Ç (—Ç–µ–Ω–≥–µ)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
            min="0"
          />
          <button
            onClick={handleFilter}
            onTouchStart={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            –ü–æ–¥–æ–±—Ä–∞—Ç—å
          </button>
        </div>

        {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –∑–∞–≥—Ä—É–∑–∫–∏ */}
        {loading && <p className="text-blue-500 mb-4">–ó–∞–≥—Ä—É–∑–∫–∞...</p>}

        {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–æ–∫ */}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {/* –ü–æ–ª–Ω—ã–µ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ –ø–æ –≤–∞—à–µ–º—É –±—é–¥–∂–µ—Ç—É:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
