import React, { useEffect, useState } from 'react';

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);

  const categories = ['processor', 'graphicsCard', 'ram', 'storage', 'motherboard'];  // Здесь мы определяем категории

  useEffect(() => {
    fetch('http://localhost:3001/api/components')
      .then(res => res.json())
      .then(data => {
        console.log(data);  // Для отладки
        setComponents(data);
      })
      .catch(err => console.error('Ошибка загрузки:', err));
  }, []);

  const handleFilter = () => {
  const maxBudget = parseInt(budget);
  if (!maxBudget || isNaN(maxBudget)) return;

  const grouped = categories.reduce((acc, category) => {
    acc[category] = components.filter(item => item.category === category);
    return acc;
  }, {});

  // Генерируем все возможные полные конфигурации
  const allCombinations = [];
  for (let cpu of grouped.processor) {
    for (let gpu of grouped.graphicsCard) {
      for (let ram of grouped.ram) {
        for (let storage of grouped.storage) {
          for (let mb of grouped.motherboard) {
            const config = {
              processor: cpu,
              graphicsCard: gpu,
              ram: ram,
              storage: storage,
              motherboard: mb
            };
            const totalPrice = cpu.price + gpu.price + ram.price + storage.price + mb.price;
            allCombinations.push({ config, totalPrice });
          }
        }
      }
    }
  }

  // Отфильтруем те, что входят в бюджет
  const withinBudget = allCombinations.filter(c => c.totalPrice <= maxBudget);

  if (withinBudget.length > 0) {
    // Если есть подходящие — показать самые дешевые
    const sorted = withinBudget.sort((a, b) => a.totalPrice - b.totalPrice);
    setConfigurations(sorted.slice(0, 3));  // максимум 3 подходящих
    setClosestConfigs([]);
  } else {
    // Иначе показать самые близкие, но дорогие
    const overBudget = allCombinations
      .filter(c => c.totalPrice > maxBudget)
      .sort((a, b) => a.totalPrice - b.totalPrice);
    setConfigurations([]);
    setClosestConfigs(overBudget.slice(0, 3)); // максимум 3 дороже
  }
};

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
          />
          <button
            onClick={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Подобрать
          </button>
        </div>

        {/* Полные конфигурации, которые подходят под бюджет */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => {
              let totalPrice = 0;
              categories.forEach(category => {
                totalPrice += config[category].price;
              });

              return (
                <div key={index} className="mb-4">
                  <ul>
                    {Object.keys(config).map((key, i) => {
                      const component = config[key];
                      return (
                        <li key={i} className="text-lg">
                          <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {component.name} 
                          <span className="italic text-gray-500">({component.description})</span>
                        </li>
                      );
                    })}
                  </ul>
                  <p className="font-bold text-lg mt-2">Общая стоимость: {totalPrice} ₸</p>
                </div>
              );
            })}
          </div>
        ) : (
          <p className="text-gray-600">Нет подходящих комплектующих для вашего бюджета.</p>
        )}

        {/* Конфигурации, которые немного дороже бюджета */}
        {closestConfigs.length > 0 ? (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">Конфигурации, которые немного превышают ваш бюджет:</h3>
            {closestConfigs.map((config, index) => {
              let totalPrice = 0;
              categories.forEach(category => {
                totalPrice += config[category].price;
              });

              return (
                <div key={index} className="mb-4">
                  <ul>
                    {Object.keys(config).map((key, i) => {
                      const component = config[key];
                      return (
                        <li key={i} className="text-lg">
                          <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {component.name} 
                          <span className="italic text-gray-500">({component.description})</span>
                        </li>
                      );
                    })}
                  </ul>
                  <p className="font-bold text-lg mt-2">Общая стоимость: {totalPrice} ₸</p>
                  <p className="text-gray-600 mt-2">Эта конфигурация немного превышает ваш бюджет.</p>
                </div>
              );
            })}
          </div>
        ) : null}
      </div>
    </div>
  );
}

export default PCBuilder;
