import React, { useEffect, useState } from 'react';

// Объект с переводами категорий на русский язык
const categoryTranslations = {
  processor: 'Процессор',
  graphicsCard: 'Видеокарта',
  ram: 'Оперативная память',
  storage: 'Накопитель',
  motherboard: 'Материнская плата',
  case: 'Корпус',
  cooler: 'Кулер',
  monitor: 'Монитор',
  powerSupply: 'Блок питания',
  keyboard: 'Клавиатура',
  mouse: 'Мышь',
  operatingSystem: 'Операционная система',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const categories = [
    'processor',
    'graphicsCard',
    'ram',
    'storage',
    'motherboard',
    'case',
    'cooler',
    'monitor',
    'powerSupply',
    'keyboard',
    'mouse',
    'operatingSystem',
  ];

  // Нормализация данных
  const normalizeComponent = (item) => ({
    ...item,
    category: item.category ? item.category.toLowerCase().trim() : '',
    price: typeof item.price === 'string' ? parseFloat(item.price.replace(/[^0-9.]/g, '')) || 0 : Number(item.price) || 0,
    performance: Number(item.performance) || 0,
    socket: item.socket || '',
    power: Number(item.power) || 0,
    formFactor: item.formFactor || '',
    resolution: item.resolution || '',
    wattage: Number(item.wattage) || 0,
    type: item.type || '',
    version: item.version || '',
  });

  // Загрузка компонентов с API
  useEffect(() => {
    setLoading(true);
    fetch('http://192.168.1.100:3001/api/components') // Исправлено на ваш IP
      .then(res => {
        if (!res.ok) throw new Error('Ошибка загрузки компонентов');
        return res.json();
      })
      .then(data => {
        console.log('Загруженные компоненты:', data);
        if (data.length === 0) {
          setError('Нет доступных компонентов. Проверьте данные в Google Sheets.');
          setComponents([]);
          return;
        }
        const normalizedData = data.map(normalizeComponent);
        console.log('Нормализованные компоненты:', normalizedData);
        setComponents(normalizedData);
        setError(null);
      })
      .catch(err => {
        setError(err.message);
        console.error('Ошибка загрузки:', err);
      })
      .finally(() => setLoading(false));
  }, []);

  // Валидация и обработка фильтрации
  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('Пожалуйста, введите корректный положительный бюджет.');
      return;
    }
    setError(null);
    setLoading(true);

    console.log('Все компоненты перед фильтрацией:', components);
    console.log('Бюджет:', maxBudget);

    const fullConfigs = [];
    const tempClosestConfigs = [];

    // Фильтрация компонентов по категориям
    const selectedComponents = categories.reduce((acc, category) => {
      acc[category] = components.filter(item => {
        const isValidCategory = item.category === category;
        const isValidPrice = typeof item.price === 'number' && item.price <= maxBudget;
        console.log(
          `Фильтрация ${category}:`,
          item.name,
          'category match:', isValidCategory,
          'price valid:', isValidPrice,
          'item:', item
        );
        return isValidCategory && isValidPrice;
      });
      console.log(
        `Категория ${category}: ${acc[category].length} компонентов`,
        acc[category].map(item => `${item.name}: ${item.price} ₸`)
      );
      return acc;
    }, {});

    // Проверка наличия компонентов
    const hasFullConfig = categories.every(category => selectedComponents[category].length > 0);
    if (!hasFullConfig) {
      const emptyCategories = categories.filter(
        category => selectedComponents[category].length === 0
      );
      setError(
        `Не удалось сформировать конфигурации: отсутствуют компоненты в категориях: ${emptyCategories
          .map(cat => categoryTranslations[cat])
          .join(', ')}.`
      );
      setLoading(false);
      return;
    }

    // Генерация комбинаций
    const combinations = generateCombinations(selectedComponents, maxBudget);
    fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
    tempClosestConfigs.push(
      ...combinations.filter(
        config => config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 100000 // Увеличено для бюджета 200,000 ₸
      )
    );

    // Сортировка конфигураций
    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setLoading(false);
  };

  // Оптимизированная генерация комбинаций
  const generateCombinations = (componentsByCategory, maxBudget) => {
    const results = [];
    const maxCombinations = 1000;

    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (results.length >= maxCombinations) {
        console.warn('Достигнут лимит комбинаций:', results.length);
        return;
      }

      if (categoryIndex >= categories.length) {
        if (totalPrice <= maxBudget + 100000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = categories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        if (totalPrice + component.price > maxBudget + 100000) {
          return;
        }

        // Совместимость: материнская плата и процессор
        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        // Совместимость: кулер и процессор
        if (
          category === 'cooler' &&
          currentConfig.processor &&
          !component.socket.split(',').includes(currentConfig.processor.socket)
        ) {
          return;
        }

        // Совместимость: корпус и материнская плата
        if (
          category === 'case' &&
          currentConfig.motherboard &&
          !component.formFactor.split(',').includes(currentConfig.motherboard.formFactor)
        ) {
          return;
        }

        // Совместимость: блок питания
        if (
          category === 'powerSupply' &&
          currentConfig.processor &&
          currentConfig.graphicsCard
        ) {
          const totalPower = currentConfig.processor.power + currentConfig.graphicsCard.power + 100;
          if (component.wattage < totalPower) {
            return;
          }
        }

        const newPrice = totalPrice + component.price;
        const newPerformance = totalPerformance + (component.performance || 0);

        buildConfig(
          { ...currentConfig, [category]: component },
          categoryIndex + 1,
          newPrice,
          newPerformance
        );
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        {/* Поле ввода бюджета */}
        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
            min="0"
          />
          <button
            onClick={handleFilter}
            onTouchStart={handleFilter}
            disabled={loading}
            className={`bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 ${
              loading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {loading ? 'Загрузка...' : 'Подобрать'}
          </button>
        </div>

        {/* Отображение ошибок */}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {/* Полные конфигурации */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
              </div>
            ))}
          </div>
        ) : (
          !error &&
          !loading && (
            <p className="text-gray-600">Нет подходящих комплектующих для вашего бюджета.</p>
          )
        )}

        {/* Конфигурации, превышающие бюджет */}
        {closestConfigs.length > 0 && (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">
              Конфигурации, которые немного превышают ваш бюджет:
            </h3>
            {closestConfigs.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
                <p className="text-gray-600 mt-2">
                  Эта конфигурация немного превышает ваш бюджет.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
