    setLoading(true);
    fetch('http://localhost:3001/api/components')
      .then(res => {
        if (!res.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤');
        return res.json();
