import React, { useState } from 'react';

const PCBuilder = () => {
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleFetch = async () => {
    setLoading(true);
    setConfigurations([]);
    setMessage('');

    try {
      const response = await axios.post('http://localhost:3001/api/components', {
        budget: parseInt(budget),
      });

      const data = response.data;

      if (!data || data.length === 0) {
        setMessage('Нет подходящих конфигураций.');
      } else {
        setConfigurations(data);
      }
    } catch (error) {
      setMessage('Ошибка при получении данных');
    }

    setLoading(false);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Сборщик ПК</h1>

      <div className="flex gap-4 mb-6">
        <input
          type="number"
          placeholder="Введите бюджет (₸)"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <button
          onClick={handleFetch}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Найти сборки
        </button>
      </div>

      {loading && <p>Загрузка...</p>}
      {message && <p>{message}</p>}

      {configurations.map((config, index) => {
        const totalPrice = Object.values(config)
          .filter((comp) => comp && comp.price)
          .reduce((sum, comp) => sum + comp.price, 0);

        return (
          <div key={index} className="border rounded p-4 mb-4 bg-gray-100">
            <h2 className="font-bold text-lg mb-2">Конфигурация #{index + 1}</h2>
            <ul className="mb-2">
              {Object.entries(config).map(([category, component]) =>
                component ? (
                  <li key={category}>
                    {category}: {component.name} ({component.description})
                  </li>
                ) : null
              )}
            </ul>
            <p className="font-semibold">Общая сумма: {totalPrice.toLocaleString()} ₸</p>
          </div>
        );
      })}
    </div>
  );
};

export default PCBuilder;
