import React, { useEffect, useState } from 'react';

const categoryTranslations = {
  processor: 'Процессор',
  graphicsCard: 'Видеокарта',
  ram: 'Оперативная память',
  storage: 'Накопитель',
  motherboard: 'Материнская плата',
  case: 'Корпус',
  cooler: 'Кулер',
  monitor: 'Монитор',
  powerSupply: 'Блок питания',
  keyboard: 'Клавиатура',
  mouse: 'Мышь',
  operatingSystem: 'Операционная система',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState({
    processor: true,
    graphicsCard: true,
    ram: true,
    storage: true,
    motherboard: true,
    case: true,
    cooler: true,
    monitor: true,
    powerSupply: true,
    keyboard: true,
    mouse: true,
    operatingSystem: true,
  });

  const categories = Object.keys(categoryTranslations);

  const normalizeComponent = (item) => {
    const normalized = {
      ...item,
      category: item.category ? item.category.toLowerCase().trim() : '',
      price: Number(item.price) || 0,
      performance: Number(item.performance) || 0,
      socket: item.socket || '',
      power: Number(item.power) || 0,
      formFactor: item.formFactor || '',
      resolution: item.resolution || '',
      wattage: Number(item.wattage) || 0,
      type: item.type || '',
      version: item.version || '',
    };
    console.log('Нормализация компонента:', item, '→', normalized);
    return normalized;
  };

  useEffect(() => {
    const fetchComponents = async (attempt = 1, maxAttempts = 3) => {
      setLoading(true);
      try {
        const controller = new AbortController();
        const timeout = setTimeout(() => controller.abort(), 5000);

        const response = await fetch('http://localhost:3001/api/components', {
          signal: controller.signal,
        });

        clearTimeout(timeout);

        if (!response.ok) throw new Error(`HTTP ошибка: ${response.status}`);
        const data = await response.json();
        console.log('Загруженные компоненты:', data);

        if (!Array.isArray(data) || data.length === 0) {
          setError('Нет доступных компонентов. Проверьте данные в Google Sheets.');
          setComponents([]);
          return;
        }

        const normalizedData = data.map(normalizeComponent).filter(item => item.category && item.price > 0);
        console.log('Нормализованные компоненты:', normalizedData);
        setComponents(normalizedData);
        setError(null);
      } catch (err) {
        console.error('Ошибка загрузки:', err);
        if (attempt < maxAttempts) {
          console.log(`Повторная попытка ${attempt + 1}/${maxAttempts} через 2 секунды...`);
          setTimeout(() => fetchComponents(attempt + 1, maxAttempts), 2000);
        } else {
          setError('Ошибка загрузки данных. Проверьте подключение к серверу.');
          setComponents([]);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchComponents();
  }, []);

  const handleCategoryToggle = (category) => {
    setSelectedCategories(prev => ({
      ...prev,
      [category]: !prev[category],
    }));
  };

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('Пожалуйста, введите корректный положительный бюджет.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }
    if (maxBudget < 200000) {
      setError('Для данной суммы нет конфигурации. Минимальный бюджет: 200,000 ₸.');
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    setError(null);
    setLoading(true);

    console.log('Все компоненты перед фильтрацией:', components);
    console.log('Бюджет:', maxBudget);
    console.log('Выбранные категории:', selectedCategories);

    if (!components.length) {
      setError('Компоненты не загружены. Проверьте подключение к серверу.');
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    const fullConfigs = [];
    const tempClosestConfigs = [];

    const activeCategories = categories.filter(category => selectedCategories[category]);
    if (activeCategories.length === 0) {
      setError('Выберите хотя бы одну категорию.');
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    const selectedComponents = activeCategories.reduce((acc, category) => {
      acc[category] = components.filter(item => {
        return item.category === category && item.price > 0 && item.price <= maxBudget;
      });
      return acc;
    }, {});

    const hasFullConfig = activeCategories.every(category => selectedComponents[category].length > 0);
    if (!hasFullConfig) {
      const emptyCategories = activeCategories.filter(
        category => selectedComponents[category].length === 0
      );
      setError(
        `Не удалось сформировать конфигурации: отсутствуют компоненты в категориях: ${emptyCategories
          .map(cat => categoryTranslations[cat])
          .join(', ')}.`
      );
      setLoading(false);
      setConfigurations([]);
      setClosestConfigs([]);
      return;
    }

    const combinations = generateCombinations(selectedComponents, maxBudget, activeCategories);
    fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
    tempClosestConfigs.push(...combinations.filter(config =>
      config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 100000
    ));

    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setLoading(false);
  };

  const generateCombinations = (componentsByCategory, maxBudget, activeCategories) => {
    const results = [];
    const maxCombinations = 1000;

    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (results.length >= maxCombinations) return;
      if (categoryIndex >= activeCategories.length) {
        if (totalPrice <= maxBudget + 100000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = activeCategories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        if (totalPrice + component.price > maxBudget + 100000) return;

        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          selectedCategories.motherboard &&
          selectedCategories.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        if (
          category === 'cooler' &&
          currentConfig.processor &&
          selectedCategories.cooler &&
          selectedCategories.processor &&
          !component.socket.split(',').includes(currentConfig.processor.socket)
        ) {
          return;
        }

        if (
          category === 'case' &&
          currentConfig.motherboard &&
          selectedCategories.case &&
          selectedCategories.motherboard &&
          component.formFactor &&
          !component.formFactor.split(',').map(f => f.trim()).includes(currentConfig.motherboard.formFactor)
        ) {
          return;
        }

        if (
          category === 'powerSupply' &&
          selectedCategories.powerSupply &&
          currentConfig.processor &&
          selectedCategories.processor &&
          currentConfig.graphicsCard &&
          selectedCategories.graphicsCard
        ) {
          const totalPower = currentConfig.processor.power + currentConfig.graphicsCard.power + 100;
          if (component.wattage < totalPower) return;
        }

        buildConfig(
          { ...currentConfig, [category]: component },
          categoryIndex + 1,
          totalPrice + component.price,
          totalPerformance + (component.performance || 0)
        );
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2">Выберите компоненты:</h3>
          <div className="grid grid-cols-2 gap-2">
            {categories.map(category => (
              <label key={category} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={selectedCategories[category]}
                  onChange={() => handleCategoryToggle(category)}
                  className="form-checkbox h-5 w-5 text-blue-600"
                />
                <span>{categoryTranslations[category]}</span>
              </label>
            ))}
          </div>
        </div>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
            min="0"
          />
          <button
            onClick={handleFilter}
            onTouchStart={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Подобрать
          </button>
        </div>

        {loading && <p className="text-blue-500 mb-4">Загрузка...</p>}
        {error && <p className="text-red-500 mb-4">{error}</p>}

        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-xl mb-4">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
              </div>
            ))}
          </div>
        ) : (
          !error && !loading && (
            <p className="text-gray-600">Нет подходящих комплектующих для вашего бюджета.</p>
          )
        )}

        {closestConfigs.length > 0 && (
          <div className="mt-6">
            <h3 className="font-bold text-xl mb-4">Конфигурации, которые немного превышают ваш бюджет:</h3>
            {closestConfigs.map((config, index) => (
              <div key={index} className="mb-6 p-4 border rounded">
                <ul>
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong>{categoryTranslations[category]}:</strong> {component.name}{' '}
                        <span className="italic text-gray-500">({component.description})</span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-lg mt-2">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
                <p className="text-gray-600 mt-2">
                  Эта конфигурация немного превышает ваш бюджет.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
