import React, { useEffect, useState } from 'react';

// –û–±—ä–µ–∫—Ç —Å –ø–µ—Ä–µ–≤–æ–¥–∞–º–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–π –Ω–∞ —Ä—É—Å—Å–∫–∏–π —è–∑—ã–∫
const categoryTranslations = {
  processor: '–ü—Ä–æ—Ü–µ—Å—Å–æ—Ä',
  graphicsCard: '–í–∏–¥–µ–æ–∫–∞—Ä—Ç–∞',
  ram: '–û–ø–µ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–∞–º—è—Ç—å',
  storage: '–ù–∞–∫–æ–ø–∏—Ç–µ–ª—å',
  motherboard: '–ú–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞',
  case: '–ö–æ—Ä–ø—É—Å',
  cooler: '–ö—É–ª–µ—Ä',
  monitor: '–ú–æ–Ω–∏—Ç–æ—Ä',
  powerSupply: '–ë–ª–æ–∫ –ø–∏—Ç–∞–Ω–∏—è',
  keyboard: '–ö–ª–∞–≤–∏–∞—Ç—É—Ä–∞',
  mouse: '–ú—ã—à—å',
  operatingSystem: '–û–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [progressText, setProgressText] = useState('');
  const [theme, setTheme] = useState('light');

  const categories = [
    'processor',
    'graphicsCard',
    'ram',
    'storage',
    'motherboard',
    'case',
    'cooler',
    'monitor',
    'powerSupply',
    'keyboard',
    'mouse',
    'operatingSystem',
  ];

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Ç–µ–º—ã
  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  // –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö
  const normalizeComponent = (item) => {
    const normalized = {
      ...item,
      category: item.category ? item.category.toLowerCase().trim() : '',
      price: typeof item.price === 'string' ? parseFloat(item.price.replace(/[^0-9.]/g, '')) || 0 : Number(item.price) || 0,
      performance: Number(item.performance) || 0,
      socket: item.socket || '',
      power: Number(item.power) || 0,
      formFactor: item.formFactor || '',
      resolution: item.resolution || '',
      wattage: Number(item.wattage) || 0,
      type: item.type || '',
      version: item.version || '',
    };
    console.log('–ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞:', item, '‚Üí', normalized); // –î–æ–±–∞–≤–ª–µ–Ω–æ –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
    return normalized;
  };

  // –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ —Å API
  useEffect(() => {
    setLoading(true);
    fetch('http://192.168.1.100:3001/api/components')
      .then(res => {
        if (!res.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤');
        return res.json();
      })
      .then(data => {
        console.log('–ó–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã (—Å—ã—Ä—ã–µ):', data);
        if (data.length === 0) {
          setError('–ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –¥–∞–Ω–Ω—ã–µ –≤ Google Sheets.');
          setComponents([]);
          return;
        }
        const normalizedData = data.map(normalizeComponent);
        console.log('–ù–æ—Ä–º–∞–ª–∏–∑–æ–≤–∞–Ω–Ω—ã–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã:', normalizedData);
        setComponents(normalizedData);
        setError(null);
      })
      .catch(err => {
        setError(err.message);
        console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err);
      })
      .finally(() => setLoading(false));
  }, []);

  // –í–∞–ª–∏–¥–∞—Ü–∏—è –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–π –±—é–¥–∂–µ—Ç.');
      return;
    }
    setError(null);
    setLoading(true);
    setProgress(0);
    setProgressText('–ó–∞–ø—É—Å–∫–∞–µ–º –ø–æ–¥–±–æ—Ä...');

    console.log('–í—Å–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –ø–µ—Ä–µ–¥ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–µ–π:', components); // –î–æ–±–∞–≤–ª–µ–Ω–æ –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
    console.log('–ë—é–¥–∂–µ—Ç:', maxBudget);

    const fullConfigs = [];
    const tempClosestConfigs = [];

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
    const selectedComponents = categories.reduce((acc, category) => {
      acc[category] = components.filter(item => {
        const isValidCategory = item.category === category;
        const isValidPrice = typeof item.price === 'number' && item.price <= maxBudget;
        console.log(
          `–§–∏–ª—å—Ç—Ä–∞—Ü–∏—è ${category}:`,
          item.name,
          'category match:', isValidCategory,
          'price valid:', isValidPrice,
          'item:', item
        ); // –î–æ–±–∞–≤–ª–µ–Ω–æ –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
        return isValidCategory && isValidPrice;
      });
      console.log(
        `–ö–∞—Ç–µ–≥–æ—Ä–∏—è ${category}: ${acc[category].length} –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤`,
        acc[category].map(item => `${item.name}: ${item.price} ‚Ç∏`)
      );
      return acc;
    }, {});

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
    const hasFullConfig = categories.every(category => selectedComponents[category].length > 0);
    if (!hasFullConfig) {
      const emptyCategories = categories.filter(
        category => selectedComponents[category].length === 0
      );
      setError(
        `–ù–µ —É–¥–∞–ª–æ—Å—å —Å—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞—Ç—å –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏: –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –≤ –∫–∞—Ç–µ–≥–æ—Ä–∏—è—Ö: ${emptyCategories
          .map(cat => categoryTranslations[cat])
          .join(', ')}.`
      );
      setLoading(false);
      return;
    }

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–º–±–∏–Ω–∞—Ü–∏–π
    const combinations = generateCombinations(selectedComponents, maxBudget);
    fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
    tempClosestConfigs.push(
      ...combinations.filter(
        config => config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 100000 // –£–≤–µ–ª–∏—á–µ–Ω–æ –¥–æ +100,000 ‚Ç∏
      )
    );

    // –ó–∞–≤–µ—Ä—à–µ–Ω–∏–µ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
    setProgress(100);
    setProgressText('–ì–æ—Ç–æ–≤–æ!');
    setTimeout(() => {
      setLoading(false);
      setProgress(0);
      setProgressText('');
    }, 500);

    // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–π
    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
  };

  // –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–∞—è –≥–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–º–±–∏–Ω–∞—Ü–∏–π
  const generateCombinations = (componentsByCategory, maxBudget) => {
    const results = [];
    const maxCombinations = 1000;
    let startTime = Date.now();
    let combinationCount = 0;

    const progressMessages = categories.map(cat => `–ü–æ–¥–±–∏—Ä–∞–µ–º ${categoryTranslations[cat].toLowerCase()}...`);

    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (results.length >= maxCombinations) {
        console.warn('–î–æ—Å—Ç–∏–≥–Ω—É—Ç –ª–∏–º–∏—Ç –∫–æ–º–±–∏–Ω–∞—Ü–∏–π:', results.length);
        return;
      }
      if (Date.now() - startTime > 5000) {
        console.warn('–ü—Ä–µ–≤—ã—à–µ–Ω –ª–∏–º–∏—Ç –≤—Ä–µ–º–µ–Ω–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏');
        return;
      }

      if (combinationCount % 100 === 0) {
        const progressPercent = Math.min(95, (combinationCount / maxCombinations) * 100);
        setProgress(progressPercent);
        setProgressText(progressMessages[categoryIndex] || '–ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏...');
      }
      combinationCount++;

      if (categoryIndex >= categories.length) {
        if (totalPrice <= maxBudget + 100000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = categories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        if (totalPrice + component.price > maxBudget + 100000) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –º–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞ –∏ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä
        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –∫—É–ª–µ—Ä –∏ –ø—Ä–æ—Ü–µ—Å—Å–æ—Ä
        if (
          category === 'cooler' &&
          currentConfig.processor &&
          !component.socket.split(',').includes(currentConfig.processor.socket)
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –∫–æ—Ä–ø—É—Å –∏ –º–∞—Ç–µ—Ä–∏–Ω—Å–∫–∞—è –ø–ª–∞—Ç–∞
        if (
          category === 'case' &&
          currentConfig.motherboard &&
          !component.formFactor.split(',').includes(currentConfig.motherboard.formFactor)
        ) {
          return;
        }

        // –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å: –±–ª–æ–∫ –ø–∏—Ç–∞–Ω–∏—è
        if (
          category === 'powerSupply' &&
          currentConfig.processor &&
          currentConfig.graphicsCard
        ) {
          const totalPower = currentConfig.processor.power + currentConfig.graphicsCard.power + 100;
          if (component.wattage < totalPower) {
            return;
          }
        }

        const newPrice = totalPrice + component.price;
        const newPerformance = totalPerformance + (component.performance || 0);

        buildConfig(
          { ...currentConfig, [category]: component },
          categoryIndex + 1,
          newPrice,
          newPerformance
        );
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div
      className={`min-h-screen p-6 transition-colors duration-300 ${
        theme === 'light'
          ? 'bg-gradient-to-br from-blue-50 to-gray-100'
          : 'bg-gradient-to-br from-gray-900 to-blue-950'
      }`}
    >
      <div
        className={`max-w-4xl mx-auto bg-white/80 dark:bg-gray-800/80 backdrop-blur-lg shadow-xl rounded-2xl p-8 transition-all duration-300 ${
          theme === 'light' ? 'text-gray-900' : 'text-gray-100'
        }`}
      >
        {/* –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å —Ç–µ–º—ã */}
        <button
          onClick={toggleTheme}
          className="absolute top-4 right-4 p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-transform hover:scale-110"
        >
          {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        </button>

        <h2 className="text-3xl font-bold mb-6 flex items-center gap-2">
          <span role="img" aria-label="computer">üõ†</span> –ü–æ–¥–±–æ—Ä–∫–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–∞
        </h2>

        {/* –ü–æ–ª–µ –≤–≤–æ–¥–∞ –±—é–¥–∂–µ—Ç–∞ */}
        <div className="flex flex-col sm:flex-row items-center gap-4 mb-8">
          <input
            type="number"
            placeholder="–í–≤–µ–¥–∏—Ç–µ –±—é–¥–∂–µ—Ç (—Ç–µ–Ω–≥–µ)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className={`border-2 rounded-lg px-4 py-3 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all ${
              theme === 'light' ? 'bg-white border-gray-300' : 'bg-gray-700 border-gray-600'
            }`}
            min="0"
          />
          <button
            onClick={handleFilter}
            onTouchStart={handleFilter}
            disabled={loading}
            className={`px-6 py-3 rounded-lg font-semibold w-full sm:w-auto transition-transform hover:scale-105 ${
              loading
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white'
            }`}
          >
            {loading ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : '–ü–æ–¥–æ–±—Ä–∞—Ç—å'}
          </button>
        </div>

        {/* –ü—Ä–æ–≥—Ä–µ—Å—Å-–±–∞—Ä */}
        {loading && (
          <div className="mb-6">
            <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">{progressText}</div>
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5">
              <div
                className="bg-gradient-to-r from-blue-600 to-indigo-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
          </div>
        )}

        {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–æ–∫ */}
        {error && (
          <p className="text-red-500 bg-red-100 dark:bg-red-900/50 rounded-lg p-4 mb-6 animate-pulse">
            {error}
          </p>
        )}

        {/* –ü–æ–ª–Ω—ã–µ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-2xl mb-6">–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ –ø–æ –≤–∞—à–µ–º—É –±—é–¥–∂–µ—Ç—É:</h3>
            {configurations.map((config, index) => (
              <div
                key={index}
                className="mb-8 p-6 bg-white/50 dark:bg-gray-900/50 rounded-xl shadow-lg transform transition-all duration-500 hover:scale-[1.02] animate-slide-in"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <ul className="space-y-3">
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong className="font-semibold">{categoryTranslations[category]}:</strong>{' '}
                        {component.name}{' '}
                        <span className="italic text-gray-500 dark:text-gray-400">
                          ({component.description})
                        </span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-xl mt-4">
                  –û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {config.totalPrice} ‚Ç∏
                </p>
                <p className="text-gray-600 dark:text-gray-400">
                  –ü—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: {config.totalPerformance} (—É—Å–ª–æ–≤–Ω—ã–µ –µ–¥–∏–Ω–∏—Ü—ã)
                </p>
              </div>
            ))}
          </div>
        ) : (
          !error &&
          !loading && (
            <p className="text-gray-600 dark:text-gray-400 bg-white/50 dark:bg-gray-900/50 rounded-lg p-4">
              –ù–µ—Ç –ø–æ–¥—Ö–æ–¥—è—â–∏—Ö –∫–æ–º–ø–ª–µ–∫—Ç—É—é—â–∏—Ö –¥–ª—è –≤–∞—à–µ–≥–æ –±—é–¥–∂–µ—Ç–∞.
            </p>
          )
        )}

        {/* –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –ø—Ä–µ–≤—ã—à–∞—é—â–∏–µ –±—é–¥–∂–µ—Ç */}
        {closestConfigs.length > 0 && (
          <div className="mt-8">
            <h3 className="font-bold text-2xl mb-6">
              –ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞—é—Ç –≤–∞—à –±—é–¥–∂–µ—Ç:
            </h3>
            {closestConfigs.map((config, index) => (
              <div
                key={index}
                className="mb-8 p-6 bg-white/50 dark:bg-gray-900/50 rounded-xl shadow-lg transform transition-all duration-500 hover:scale-[1.02] animate-slide-in"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <ul className="space-y-3">
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong className="font-semibold">{categoryTranslations[category]}:</strong>{' '}
                        {component.name}{' '}
                        <span className="italic text-gray-500 dark:text-gray-400">
                          ({component.description})
                        </span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-xl mt-4">
                  –û–±—â–∞—è —Å—Ç–æ–∏–º–æ—Å—Ç—å: {config.totalPrice} ‚Ç∏
                </p>
                <p className="text-gray-600 dark:text-gray-400">
                  –ü—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: {config.totalPerformance} (—É—Å–ª–æ–≤–Ω—ã–µ –µ–¥–∏–Ω–∏—Ü—ã)
                </p>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  –≠—Ç–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –Ω–µ–º–Ω–æ–≥–æ –ø—Ä–µ–≤—ã—à–∞–µ—Ç –≤–∞—à –±—é–¥–∂–µ—Ç.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
