import React, { useEffect, useState } from 'react';

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/components')
      .then(res => res.json())
      .then(data => setComponents(data))
      .catch(err => console.error('Ошибка загрузки:', err));
  }, []);

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget) return;

    const sorted = [...components].sort((a, b) => b.price - a.price);

    const selected = [];
    let total = 0;
    const usedCategories = new Set();

    for (let item of sorted) {
      if (!usedCategories.has(item.category) && total + item.price <= maxBudget) {
        selected.push(item);
        total += item.price;
        usedCategories.add(item.category);
      }
    }

    setFiltered(selected);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Подборка компьютера</h2>
      <input
        type="number"
        placeholder="Введите бюджет (тенге)"
        value={budget}
        onChange={e => setBudget(e.target.value)}
        style={{ marginRight: 10 }}
      />
      <button onClick={handleFilter}>Подобрать</button>

      <h3>Результаты:</h3>
      {filtered.length > 0 ? (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Категория</th>
              <th>Название</th>
              <th>Цена</th>
              <th>Описание</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map((c, i) => (
              <tr key={i}>
                <td>{c.category}</td>
                <td>{c.name}</td>
                <td>{c.price}</td>
                <td>{c.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Нет данных или не выбрано</p>
      )}
    </div>
  );
}

export default PCBuilder;
