import React, { useEffect, useState } from 'react';

// Объект с переводами категорий на русский язык
const categoryTranslations = {
  processor: 'Процессор',
  graphicsCard: 'Видеокарта',
  ram: 'Оперативная память',
  storage: 'Накопитель',
  motherboard: 'Материнская плата',
};

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [configurations, setConfigurations] = useState([]);
  const [closestConfigs, setClosestConfigs] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [progressText, setProgressText] = useState('');
  const [theme, setTheme] = useState('light');

  const categories = ['processor', 'graphicsCard', 'ram', 'storage', 'motherboard'];

  // Переключение темы
  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  // Нормализация данных
  const normalizeComponent = (item) => ({
    ...item,
    category: item.category ? item.category.toLowerCase().trim() : '',
    price: typeof item.price === 'string' ? parseFloat(item.price.replace(/[^0-9.]/g, '')) || 0 : Number(item.price) || 0,
    performance: Number(item.performance) || 0,
    socket: item.socket || '',
  });

  // Загрузка компонентов с API
  useEffect(() => {
    setLoading(true);
    fetch('http://192.168.1.100:3001/api/components') // Замените на ваш IP
      .then(res => {
        if (!res.ok) throw new Error('Ошибка загрузки компонентов');
        return res.json();
      })
      .then(data => {
        console.log('Загруженные компоненты (сырые):', data);
        if (data.length === 0) {
          setError('Нет доступных компонентов. Проверьте данные в Google Sheets.');
          setComponents([]);
          return;
        }
        const normalizedData = data.map(normalizeComponent);
        console.log('Нормализованные компоненты:', normalizedData);
        setComponents(normalizedData);
        setError(null);
      })
      .catch(err => {
        setError(err.message);
        console.error('Ошибка загрузки:', err);
      })
      .finally(() => setLoading(false));
  }, []);

  // Валидация и обработка фильтрации
  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget || isNaN(maxBudget) || maxBudget <= 0) {
      setError('Пожалуйста, введите корректный положительный бюджет.');
      return;
    }
    setError(null);
    setLoading(true);
    setProgress(0);
    setProgressText('Запускаем подбор...');

    const fullConfigs = [];
    const tempClosestConfigs = [];

    // Фильтрация компонентов по категориям
    const selectedComponents = categories.reduce((acc, category) => {
      acc[category] = components.filter(item => {
        const isValidCategory = item.category === category;
        const isValidPrice = typeof item.price === 'number' && item.price <= maxBudget;
        return isValidCategory && isValidPrice;
      });
      console.log(
        `Категория ${category}: ${acc[category].length} компонентов`,
        acc[category].map(item => `${item.name}: ${item.price} ₸`)
      );
      return acc;
    }, {});

    // Проверка наличия компонентов
    const hasFullConfig = categories.every(category => selectedComponents[category].length > 0);
    if (!hasFullConfig) {
      const emptyCategories = categories.filter(
        category => selectedComponents[category].length === 0
      );
      setError(
        `Не удалось сформировать конфигурации: отсутствуют компоненты в категориях: ${emptyCategories
          .map(cat => categoryTranslations[cat])
          .join(', ')}.`
      );
      setLoading(false);
      return;
    }

    // Генерация комбинаций
    const combinations = generateCombinations(selectedComponents, maxBudget);
    fullConfigs.push(...combinations.filter(config => config.totalPrice <= maxBudget));
    tempClosestConfigs.push(
      ...combinations.filter(
        config => config.totalPrice > maxBudget && config.totalPrice <= maxBudget + 20000
      )
    );

    // Завершение прогресса
    setProgress(100);
    setProgressText('Готово!');
    setTimeout(() => {
      setLoading(false);
      setProgress(0);
      setProgressText('');
    }, 500);

    // Сортировка конфигураций
    setConfigurations(
      fullConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
    setClosestConfigs(
      tempClosestConfigs
        .sort((a, b) => b.totalPerformance - a.totalPerformance || a.totalPrice - b.totalPrice)
        .slice(0, 3)
    );
  };

  // Оптимизированная генерация комбинаций
  const generateCombinations = (componentsByCategory, maxBudget) => {
    const results = [];
    const maxCombinations = 500;
    let startTime = Date.now();
    let combinationCount = 0;

    const progressMessages = [
      'Подбираем процессор...',
      'Выбираем видеокарту...',
      'Настраиваем оперативную память...',
      'Добавляем накопитель...',
      'Финализируем материнскую плату...',
    ];

    const buildConfig = (currentConfig, categoryIndex, totalPrice, totalPerformance) => {
      if (results.length >= maxCombinations) {
        console.warn('Достигнут лимит комбинаций:', results.length);
        return;
      }
      if (Date.now() - startTime > 5000) {
        console.warn('Превышен лимит времени генерации');
        return;
      }

      if (combinationCount % 100 === 0) {
        const progressPercent = Math.min(95, (combinationCount / maxCombinations) * 100);
        setProgress(progressPercent);
        setProgressText(progressMessages[categoryIndex] || 'Генерируем конфигурации...');
      }
      combinationCount++;

      if (categoryIndex >= categories.length) {
        if (totalPrice <= maxBudget + 20000) {
          results.push({ ...currentConfig, totalPrice, totalPerformance });
        }
        return;
      }

      const category = categories[categoryIndex];
      componentsByCategory[category].forEach(component => {
        if (totalPrice + component.price > maxBudget + 20000) {
          return;
        }

        if (
          category === 'motherboard' &&
          currentConfig.processor &&
          component.socket !== currentConfig.processor.socket
        ) {
          return;
        }

        const newPrice = totalPrice + component.price;
        const newPerformance = totalPerformance + (component.performance || 0);

        buildConfig(
          { ...currentConfig, [category]: component },
          categoryIndex + 1,
          newPrice,
          newPerformance
        );
      });
    };

    buildConfig({}, 0, 0, 0);
    return results;
  };

  return (
    <div
      className={`min-h-screen p-6 transition-colors duration-300 ${
        theme === 'light'
          ? 'bg-gradient-to-br from-blue-50 to-gray-100'
          : 'bg-gradient-to-br from-gray-900 to-blue-950'
      }`}
    >
      <div
        className={`max-w-4xl mx-auto bg-white/80 dark:bg-gray-800/80 backdrop-blur-lg shadow-xl rounded-2xl p-8 transition-all duration-300 ${
          theme === 'light' ? 'text-gray-900' : 'text-gray-100'
        }`}
      >
        {/* Переключатель темы */}
        <button
          onClick={toggleTheme}
          className="absolute top-4 right-4 p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-transform hover:scale-110"
        >
          {theme === 'light' ? '🌙' : '☀️'}
        </button>

        <h2 className="text-3xl font-bold mb-6 flex items-center gap-2">
          <span role="img" aria-label="computer">🛠</span> Подборка компьютера
        </h2>

        {/* Поле ввода бюджета */}
        <div className="flex flex-col sm:flex-row items-center gap-4 mb-8">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className={`border-2 rounded-lg px-4 py-3 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all ${
              theme === 'light' ? 'bg-white border-gray-300' : 'bg-gray-700 border-gray-600'
            }`}
            min="0"
          />
          <button
            onClick={handleFilter}
            onTouchStart={handleFilter}
            disabled={loading}
            className={`px-6 py-3 rounded-lg font-semibold w-full sm:w-auto transition-transform hover:scale-105 ${
              loading
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white'
            }`}
          >
            {loading ? 'Загрузка...' : 'Подобрать'}
          </button>
        </div>

        {/* Прогресс-бар */}
        {loading && (
          <div className="mb-6">
            <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">{progressText}</div>
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5">
              <div
                className="bg-gradient-to-r from-blue-600 to-indigo-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
          </div>
        )}

        {/* Отображение ошибок */}
        {error && (
          <p className="text-red-500 bg-red-100 dark:bg-red-900/50 rounded-lg p-4 mb-6 animate-pulse">
            {error}
          </p>
        )}

        {/* Полные конфигурации */}
        {configurations.length > 0 ? (
          <div>
            <h3 className="font-bold text-2xl mb-6">Конфигурации по вашему бюджету:</h3>
            {configurations.map((config, index) => (
              <div
                key={index}
                className="mb-8 p-6 bg-white/50 dark:bg-gray-900/50 rounded-xl shadow-lg transform transition-all duration-500 hover:scale-[1.02] animate-slide-in"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <ul className="space-y-3">
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong className="font-semibold">{categoryTranslations[category]}:</strong>{' '}
                        {component.name}{' '}
                        <span className="italic text-gray-500 dark:text-gray-400">
                          ({component.description})
                        </span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-xl mt-4">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600 dark:text-gray-400">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
              </div>
            ))}
          </div>
        ) : (
          !error &&
          !loading && (
            <p className="text-gray-600 dark:text-gray-400 bg-white/50 dark:bg-gray-900/50 rounded-lg p-4">
              Нет подходящих комплектующих для вашего бюджета.
            </p>
          )
        )}

        {/* Конфигурации, превышающие бюджет */}
        {closestConfigs.length > 0 && (
          <div className="mt-8">
            <h3 className="font-bold text-2xl mb-6">
              Конфигурации, которые немного превышают ваш бюджет:
            </h3>
            {closestConfigs.map((config, index) => (
              <div
                key={index}
                className="mb-8 p-6 bg-white/50 dark:bg-gray-900/50 rounded-xl shadow-lg transform transition-all duration-500 hover:scale-[1.02] animate-slide-in"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <ul className="space-y-3">
                  {categories.map((category, i) => {
                    const component = config[category];
                    return component ? (
                      <li key={i} className="text-lg">
                        <strong className="font-semibold">{categoryTranslations[category]}:</strong>{' '}
                        {component.name}{' '}
                        <span className="italic text-gray-500 dark:text-gray-400">
                          ({component.description})
                        </span>
                      </li>
                    ) : null;
                  })}
                </ul>
                <p className="font-bold text-xl mt-4">
                  Общая стоимость: {config.totalPrice} ₸
                </p>
                <p className="text-gray-600 dark:text-gray-400">
                  Производительность: {config.totalPerformance} (условные единицы)
                </p>
                <p className="text-gray-600 dark:text-gray-400 mt-2">
                  Эта конфигурация немного превышает ваш бюджет.
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
