import React, { useEffect, useState } from 'react';

function PCBuilder() {
  const [components, setComponents] = useState([]);
  const [budget, setBudget] = useState('');
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/components')
      .then(res => res.json())
      .then(data => setComponents(data))
      .catch(err => console.error('Ошибка загрузки:', err));
  }, []);

  const handleFilter = () => {
    const maxBudget = parseInt(budget);
    if (!maxBudget) return;

    const sorted = [...components].sort((a, b) => b.price - a.price);

    const selected = [];
    let total = 0;
    const usedCategories = new Set();

    for (let item of sorted) {
      if (!usedCategories.has(item.category) && total + item.price <= maxBudget) {
        selected.push(item);
        total += item.price;
        usedCategories.add(item.category);
      }
    }

    setFiltered(selected);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded p-6">
        <h2 className="text-2xl font-bold mb-4">🛠 Подборка компьютера</h2>

        <div className="flex items-center space-x-4 mb-6">
          <input
            type="number"
            placeholder="Введите бюджет (тенге)"
            value={budget}
            onChange={e => setBudget(e.target.value)}
            className="border rounded px-4 py-2 w-full"
          />
          <button
            onClick={handleFilter}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Подобрать
          </button>
        </div>

        {filtered.length > 0 ? (
          <table className="min-w-full table-auto border border-gray-300">
            <thead className="bg-gray-200">
              <tr>
                <th className="px-4 py-2 text-left">Категория</th>
                <th className="px-4 py-2 text-left">Название</th>
                <th className="px-4 py-2 text-left">Цена</th>
                <th className="px-4 py-2 text-left">Описание</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((c, i) => (
                <tr key={i} className="border-t">
                  <td className="px-4 py-2">{c.category}</td>
                  <td className="px-4 py-2">{c.name}</td>
                  <td className="px-4 py-2">{c.price.toLocaleString()} ₸</td>
                  <td className="px-4 py-2">{c.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-600">Нет результатов. Укажите бюджет и нажмите "Подобрать".</p>
        )}
      </div>
    </div>
  );
}

export default PCBuilder;
